import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import GestoreChiamate from "./GestoreChiamate";
import "./App.css";

const unitaArr = [
  { id: 1, nome: "Polizia" },
  { id: 2, nome: "Carabinieri" },
  { id: 3, nome: "Vigili Del Fuoco" },
  { id: 4, nome: "Ambulanza" },
];

function Home() {
  return (
    <div className="home-container">
      <h1 className="title">Gestione Emergenze</h1>
      <div className="button-container">
        <Link to="/gestore-chiamate" className="btn">üìã Gestisci Chiamate</Link>
        <Link to="/invia-emergenza" className="btn">üö® Invia Emergenza</Link>
      </div>
    </div>
  );
}

function InviaEmergenza({ aggiungiChiamata }) {
  const [unita, setUnita] = useState("Polizia");
  const [descrizione, setDescrizione] = useState("");
  const [via, setVia] = useState("");
  const [errore, setErrore] = useState("");
  const [successo, setSuccesso] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrore("");
    setSuccesso(false);
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(via)}`
      );
      const data = await response.json();
      if (data.length > 0) {
        const lat = parseFloat(data[0].lat);
        const lng = parseFloat(data[0].lon);
        aggiungiChiamata({ unita, descrizione, via, lat, lng });
        setDescrizione("");
        setVia("");
        setSuccesso(true);
        setTimeout(() => setSuccesso(false), 3000);
      } else {
        setErrore("Indirizzo non trovato.");
      }
    } catch (error) {
      console.error("Errore durante la richiesta API: ", error);
      setErrore("Errore di connessione.");
    }
  };

  return (
    <div className="content-container">
      <h1 className="title">üö® Invia Emergenza</h1>
      <form onSubmit={handleSubmit} className="emergency-form">
        <select className="input-field" value={unita} onChange={(e) => setUnita(e.target.value)}>
          {unitaArr.map((u) => (
            <option key={u.id} value={u.nome}>{u.nome}</option>
          ))}
        </select>
        <input type="text" placeholder="Descrizione" value={descrizione} onChange={(e) => setDescrizione(e.target.value)} required className="input-field" />
        <input type="text" placeholder="Via" value={via} onChange={(e) => setVia(e.target.value)} required className="input-field" />
        <button type="submit" className="btn-submit">üöÄ Invia</button>
        {errore && <p className="errore">{errore}</p>}
        {successo && <p className="notifica-successo">‚úÖ Chiamata inviata con successo!</p>}
      </form>
      <Link to="/" className="btn">‚¨ÖÔ∏è Torna Indietro</Link>
    </div>
  );
}

function App() {
  const [chiamate, setChiamate] = useState([]);

  // ‚úÖ Carica chiamate dal backend al primo avvio
  useEffect(() => {
    fetch("http://localhost:3001/api/chiamate")
      .then((res) => res.json())
      .then((data) => setChiamate(data))
      .catch((err) => console.error("Errore nel caricamento chiamate:", err));
  }, []);

  // ‚úÖ Salva nuova chiamata nel backend
  const aggiungiChiamata = (nuovaChiamata) => {
    fetch("http://localhost:3001/api/chiamate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(nuovaChiamata),
    })
      .then((res) => res.json())
      .then((data) => setChiamate((prev) => [data, ...prev]))
      .catch((err) => console.error("Errore nell'invio chiamata:", err));
  };

  return (
    <div className="app-background">
      <Router>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/gestore-chiamate" element={<GestoreChiamate chiamate={chiamate} />} />
          <Route path="/invia-emergenza" element={<InviaEmergenza aggiungiChiamata={aggiungiChiamata} />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
